from docutils import nodes
from sphinx.util.docutils import SphinxDirective
import uuid


class CASDirective(SphinxDirective):
    # Require two arguments: width and height.
    required_arguments = 2
    optional_arguments = 0
    has_content = False

    def run(self):
        # Convert arguments to integers (with defaults if conversion fails)
        try:
            width = int(self.arguments[0])
        except ValueError:
            width = 700
        try:
            height = int(self.arguments[1])
        except ValueError:
            height = 400

        # Generate a unique container ID using a short UUID.
        container_id = f"ggb-cas-{uuid.uuid4().hex[:8]}"

        # Create the raw HTML. Adjust the ggbBase64 value as needed.
        html = f"""
<div id="{container_id}" style="width: {width}px; height: {height}px;"></div>
<script>
document.addEventListener("DOMContentLoaded", function() {{
    var options = {{
        appName: 'classic',
        width: {width},
        height: {height},
        showMenuBar: false,
        showAlgebraInput: false,
        showToolBar: true,
        enableLabelDrags: false,
        enableShiftDragZoom: true,
        enableRightClick: false,
        errorDialogsActive: false,
        useBrowserForJS: false,
        showKeyboardOnFocus: false,
        language: "nb",
        ggbBase64: "", 

        appletOnLoad: function(api) {{
            console.log("GeoGebra applet loaded for {container_id}");
            api.registerUpdateListener(function(objName) {{
                console.log(objName + " was updated.");
            }});
            
        }}
    }};
    var applet = new GGBApplet(options, true);
    applet.inject("{container_id}");
}});
</script>
        """
        return [nodes.raw("", html, format="html")]


def setup(app):
    app.add_directive("cas", CASDirective)
    return {
        "version": "0.1",
        "parallel_read_safe": True,
        "parallel_write_safe": True,
    }
